import React from 'react';

import './Main.css';

export default function Main({cityCOL1, cityCOL2, cityName1, cityName2, salary1, salary2}) {
    const calcAdjustedSal = (salary, col) => {
        return salary * (col/100);
    }

    const formatSalary = (salary) => {
        return parseInt(salary.replace(/[$,]/g, "").replace("$", ""));
    }

    const reFormatSalary = (salary) => {
        // Convert the salary to a number and format it with commas
        const formattedSalary = Number(salary).toLocaleString();

        // Add the '$' symbol
        return '$' + formattedSalary;
    }

    const betterJob = (salary1, salary2) => {
        return salary1 > salary2 ? "1" : "2";
    }

    const adjSalary1 = calcAdjustedSal(formatSalary(salary1), cityCOL1);
    const adjSalary2 = calcAdjustedSal(formatSalary(salary2), cityCOL2);

    return (
        <div className='main'>
            <div className='section1'>
                <p className='section1-text'>
                    Job #{betterJob(adjSalary1, adjSalary2)} pays more than Job #{betterJob(adjSalary2, adjSalary1)} by ${Math.abs(adjSalary1 - adjSalary2).toFixed(2)}.
                </p>
            </div>
            <div className='section2'>
                <div className='job1'>
                    <div className='job-header'>
                        <p>Job #1</p>
                    </div>
                    <div className='salary'>
                        <p>Salary</p>
                        <p>{salary1}</p>
                    </div>
                    <div className='city'>
                        <p>City</p>
                        <p>{cityName1}</p>
                    </div>
                    <div className='adjSal'>
                        <p>Adjusted Salary</p>
                        <p>{adjSalary1}</p>
                    </div>
                </div>
                <div className='job2'>
                    <div className='job-header'>
                        <p>Job #2</p>
                    </div>
                    <div className='salary'>
                        <p>Salary</p>
                        <p>{salary2}</p>
                    </div>
                    <div className='city'>
                        <p>City</p>
                        <p>{cityName2}</p>
                    </div>
                    <div className='adjSal'>
                        <p>Adjusted Salary</p>
                        {/*<p>{reFormatSalary(adjSalary2)}</p>*/}
                    </div>
                </div>
            </div>
        </div>
    );
}
