import './OpenSidebar.css';
import chart from './chart.png';
import github from './github.png';
import x from './x.png';
import { useState } from 'react';
import Sidebar from './Sidebar';
import Content from './Content'

export default function OpenSidebar() {
    const [sidebarClosed, setSidebarClosed] = useState(false);
    const [showSidebar, hideSidebar] = useState(true);

    const handleToggleSidebar = () => {
        setSidebarClosed(!sidebarClosed);
        hideSidebar(!showSidebar);
    }

    return (<div>
        {showSidebar && (
             <div className='open-sidebar'>
             <div className='sidebar-header'>
                 <div className='logo'>
                     <img className='chart' src={chart} alt='Chart'></img>
                     <p>Salary Comp</p>
                 </div>
                 <img className='x' src={x} alt='x' onClick={handleToggleSidebar}></img>
             </div>
             <form className='form'>
                 <h1>Job #1</h1>
                 <div className='city'>
                     <label>City</label>
                     <input type='text' placeholder='Enter City'></input>
                 </div>
                 <div className='salary'>
                     <label>Salary</label>
                     <input type='text' placeholder='Enter Salary'></input>
                 </div>
                 <h1>Job #2</h1>
                 <div className='city'>
                     <label>City</label>
                     <input type='text' placeholder='Enter City'></input>
                 </div>
                 <div className='salary'>
                     <label>Salary</label>
                     <input type='text' placeholder='Enter Salary'></input>
                 </div>
             </form>
             <div className='footer'>
                 <p className='hiw'>how it works</p>
                 <img className='github' src={github} alt='github'></img>
                 <p className='copyright'>Copyright © 2023 Salary Comp. All rights reserved.</p>
             </div>
         </div>
        )} 
        {sidebarClosed ? (
        <>
            <Sidebar /> {/* Render the plus icon */}
        </>
        ) : (
        <>
            <Sidebar />
            {!sidebarClosed && <Content />}
        </>
        )}

        </div>
    )
}
