import React, { useState, useEffect } from 'react';
import plus from './plus.png';
import './Sidebar.css';
import chart from './chart.png';
import github from './github.png';
import x from './x.png';
import Select from './Select';
import './OpenSidebar.css'
import './ClosedSidebar.css'

export default function Sidebar() {
    const [isOpen, setIsOpen] = useState(false);
    const [isClosed, setIsClosed] = useState(true);

    const [city, setCity] = useState('');
    const [salary, setSalary] = useState('');

    const [error1, setError1] = useState(false);
    const [error2, setError2] = useState(false);

    useEffect(() => {
        console.log(city);        
    }, [city, salary]);

    const openSidebar = () => {
        setIsOpen(true);
        setIsClosed(false);
    }

    const closeSidebar = () => {
        setIsOpen(false);
        setIsClosed(true);
    }

    const cityChangeHandler = (event) => {
        setCity(event.target.value);
    }

    const salary1ChangeHandler = (event) => {
        if (event.target.value.trim().length === 0) {
            setError1(true);
            return;
        } else {
            setError1(false);
        }
        setSalary(event.target.value);
    }

    const salary2ChangeHandler = (event) => {
        console.log(event.target.value.typeof);
        if (event.target.value.trim().length === 0 || event.target.value < 0 || event.target.value.typeof !== 'number') {
            setError2(true);
            return;
        } else {
            setError2(false);
        }
        setSalary(formatCurrency(event.target.value));
    }

    const refresh = () => {
        window.location.reload(false);
    }

    return (
        <div className="sidebar">
            {isClosed && (
                <div className='closed-sidebar'>
                <img
                    className="plusIcon"
                    src={plus}
                    alt="plus"
                    onClick={openSidebar}
                />    
            </div>     
            )}
            {isOpen && (
                <div className='open-sidebar'>
                <div className='sidebar-header'>
                    <div className='logo' onClick={refresh}>
                        <img className='chart' src={chart} alt='Chart'></img>
                        <p>Salary Comp</p>
                    </div>
                    <img className='x' src={x} alt='x' onClick={closeSidebar}></img>
                </div>
                <form className='form'>
                    <h1>Job #1</h1>
                    <div className='city'>
                        <label>City</label>
                        <Select onChange={cityChangeHandler}/>
                    </div>
                    <div className='salary'>
                        <label>Salary</label>
                        <input className='salary-input' type='text' placeholder='Enter Salary' onChange={salary1ChangeHandler}></input>
                        {error1 && <p className='error'>Enter a correct salary value</p>}
                    </div>
                    <h1>Job #2</h1>
                    <div className='city'>
                        <label>City</label>
                        <Select onChange={cityChangeHandler}/>
                    </div>
                    <div className='salary'>
                        <label>Salary</label>
                        <input className='salary-input' type='text' placeholder='Enter Salary' onChange={salary2ChangeHandler}></input>
                        {error2 && <p className='error'>Enter a correct salary value</p>}
                    </div>
                </form>
                <div className='footer'>
                    <p className='hiw'>how it works</p>
                    <img className='github' src={github} alt='github'></img>
                    <p className='copyright'>Copyright Â© 2023 Salary Comp. All rights reserved.</p>
                </div>
            </div>
            )}
        </div>
    );
}
