import React, { useState, useEffect } from 'react';
import plus from './plus.png';
import './Sidebar.css';
import chart from './chart.png';
import github from './github.png';
import x from './x.png';
import Select from './Select';
import './OpenSidebar.css'
import './ClosedSidebar.css'

export default function Sidebar() {
    const [isOpen, setIsOpen] = useState(false);
    const [isClosed, setIsClosed] = useState(true);

    const [city, setCity] = useState('');
    const [salary, setSalary] = useState('');

    useEffect(() => {
        console.log(city);        
    }, [city, salary]);

    const openSidebar = () => {
        setIsOpen(true);
        setIsClosed(false);
    }

    const closeSidebar = () => {
        setIsOpen(false);
        setIsClosed(true);
    }

    const cityChangeHandler = (event) => {
        setCity(event.target.value);
    }

    const salaryChangeHandler = (event) => {
        setSalary(event.target.value);
    }

    return (
        <div className="sidebar">
            {isClosed && (
                <div className='closed-sidebar'>
                <img
                    className="plusIcon"
                    src={plus}
                    alt="plus"
                    onClick={openSidebar}
                />    
            </div>     
            )}
            {isOpen && (
                <div className='open-sidebar'>
                <div className='sidebar-header'>
                    <div className='logo' onClick={refresh}>
                        <img className='chart' src={chart} alt='Chart'></img>
                        <p>Salary Comp</p>
                    </div>
                    <img className='x' src={x} alt='x' onClick={closeSidebar}></img>
                </div>
                <form className='form'>
                    <h1>Job #1</h1>
                    <div className='city'>
                        <label>City</label>
                        <Select onChange={cityChangeHandler}/>
                    </div>
                    <div className='salary'>
                        <label>Salary</label>
                        <input className='salary-input' type='text' placeholder='Enter Salary' onChange={salaryChangeHandler}></input>
                    </div>
                    <h1>Job #2</h1>
                    <div className='city'>
                        <label>City</label>
                        <Select onChange={cityChangeHandler}/>
                    </div>
                    <div className='salary'>
                        <label>Salary</label>
                        <input className='salary-input' type='text' placeholder='Enter Salary' onChange={salaryChangeHandler}></input>
                    </div>
                </form>
                <div className='footer'>
                    <p className='hiw'>how it works</p>
                    <img className='github' src={github} alt='github'></img>
                    <p className='copyright'>Copyright © 2023 Salary Comp. All rights reserved.</p>
                </div>
            </div>
            )}
        </div>
    );
}
