import React, { useState, useEffect } from 'react';
import plus from './plus.png';
import './Sidebar.css';
import chart from './chart.png';
import github from './github.png';
import x from './x.png';
import Select from './Select';
import './OpenSidebar.css';

export default function Sidebar() {
    const [isOpen, setIsOpen] = useState(false);
    const [isClosed, setIsClosed] = useState(true);

    const [city1, setCity1] = useState('');
    const [city2, setCity2] = useState('');

    const [salary1, setSalary1] = useState('');
    const [salary2, setSalary2] = useState('');


    const [error1, setError1] = useState(false);
    const [error2, setError2] = useState(false);

    const formatSalary = (salary) => {
        const number = parseFloat(salary.replace(/[^0-9.-]+/g, ''));
        const formattedNumber = number.toLocaleString('en-US', { style: 'currency', currency: 'USD' });
        return formattedNumber;
    }

    useEffect((e) => {
        console.log(e);        
    }, [city1, city2, salary1, salary2]);

    const openSidebar = () => {
        setIsOpen(true);
        setIsClosed(false);
    }

    const closeSidebar = () => {
        setIsOpen(false);
        setIsClosed(true);
    }

    const city1ChangeHandler = (event) => {
        setCity1(event.target.value);
    }

    const city2ChangeHandler = (event) => {
        setCity2(event.target.value);
    }


    const salary1ChangeHandler = (event) => {
        if (event.target.value < 0) {
            setError1(true);
        } else {
            setError1(false);
        }
        event.target.value = formatSalary(event.target.value);
        setSalary1(event.target.value);
    }

    const salary2ChangeHandler = (event) => {
        const enteredValue = event.target.value;
        const numberValue = parseFloat(enteredValue.replace(/[^0-9.-]+/g, ''));
        if (numberValue < 0) {
            setError2(true);
        } else {
            setError2(false);
        }
        formattedSalary = formatSalary(enteredValue);
        setSalary2(formattedSalary);
    }

    const refresh = () => {
        window.location.reload(false);
    }

    return (
        <div className="sidebar">
            {isClosed && (
                <div className='closed-sidebar'>
                <img
                    className="plusIcon"
                    src={plus}
                    alt="plus"
                    onClick={openSidebar}
                />    
            </div>     
            )}
            {isOpen && (
                <div className='open-sidebar'>
                <div className='sidebar-header'>
                    <div className='logo' onClick={refresh}>
                        <img className='chart' src={chart} alt='Chart'></img>
                        <p>Salary Comp</p>
                    </div>
                    <img className='x' src={x} alt='x' onClick={closeSidebar}></img>
                </div>
                <form className='form'>
                    <h1>Job #1</h1>
                    <div className='city'>
                        <label>City</label>
                        <Select onChange={city1ChangeHandler}/>
                    </div>
                    <div className='salary'>
                        <label>Salary</label>
                        <input className='salary-input' type='text' placeholder='Enter Salary' onChange={salary1ChangeHandler} value={salary1}></input>
                        {error1 && <p className='error'>Enter a correct salary value</p>}
                    </div>
                    <h1>Job #2</h1>
                    <div className='city'>
                        <label>City</label>
                        <Select onChange={city2ChangeHandler}/>
                    </div>
                    <div className='salary'>
                        <label>Salary</label>
                        <input className='salary-input' type='text' placeholder='Enter Salary' onChange={salary2ChangeHandler} value={salary2}></input>
                        {error2 && <p className='error'>Enter a correct salary value</p>}
                    </div>
                </form>
                <div className='footer'>
                    <p className='hiw'>how it works</p>
                    <img className='github' src={github} alt='github'></img>
                    <p className='copyright'>Copyright © 2023 Salary Comp. All rights reserved.</p>
                </div>
            </div>
            )}
        </div>
    );
}
