import './OpenSidebar.css';
import chart from './chart.png';
import github from './github.png';
import x from './x.png';
import Select from './Select';
import { useSidebarState } from './sidebarState';


export default function OpenSidebar() {
    const OpenSidebar = () => {
        const [isOpen, setIsOpen] = useSidebarState();
    };

    const handleButtonClick = () => {
          setIsOpen(false); // Set the shared state to closed
    };

    return (<div className='sidebar-wrapper'>
             <div className='open-sidebar'>
             <div className='sidebar-header'>
                 <div className='logo'>
                     <img className='chart' src={chart} alt='Chart'></img>
                     <p>Salary Comp</p>
                 </div>
                 <img className='x' src={x} alt='x' onClick={handleButtonClick}></img>
             </div>
             <form className='form'>
                 <h1>Job #1</h1>
                 <div className='city'>
                     <label>City</label>
                     <Select />
                 </div>
                 <div className='salary'>
                     <label>Salary</label>
                     <input type='text' placeholder='Enter Salary'></input>
                 </div>
                 <h1>Job #2</h1>
                 <div className='city'>
                     <label>City</label>
                     <Select />
                 </div>
                 <div className='salary'>
                     <label>Salary</label>
                     <input type='text' placeholder='Enter Salary'></input>
                 </div>
             </form>
             <div className='footer'>
                 <p className='hiw'>how it works</p>
                 <img className='github' src={github} alt='github'></img>
                 <p className='copyright'>Copyright Â© 2023 Salary Comp. All rights reserved.</p>
             </div>
         </div>
    </div>
    )
}
